wrapper.script:

var bitmap = value,
    checkboxes = this.options.wrapper.elements.getElementsByTagName('checkbox'),
    checkbox;
for(var i = 0, l = checkboxes.length, checkbox; i < l; i++) {
  checkbox = checkboxes[i];
  checkbox.set((parseInt(bitmap) & parseInt(checkbox.getAttribute('value'))));
}


var bitmap = 0,
    checkboxes = this.options.wrapper.elements.getElementsByTagName('checkbox'),
    checkbox;
for(var i = 0, l = checkboxes.length, checkbox; i < l; i++) {
  checkbox = checkboxes[i];
  if(checkbox.get()) {
    bitmap += parseInt(checkbox.getAttribute('value'));
  }
}
return bitmap;


var tabs = guiconfig.getPreferenceByKey("browser.sessionstore.max_tabs_undo");
var windows = guiconfig.getPreferenceByKey("browser.sessionstore.max_windows_undo");
if(is_defined(value) && !value) {
  tabs.preference.set(0);
  windows.preference.set(0);
}
else {
  if(tabs.get() == 0)
    tabs.preference.set(10);
  if(windows.get() == 0)
    windows.preference.set(3);
}
this.preference.set(value);


for(var i = 0, l = this.options.wrapper.dependencies.length; i < l; i++)
  if(!this.options.wrapper.dependencies[i].get()) return false;
return true;
